mapper.select:
SELECT p.id, p.name, p.price, p.use_amount, p.reg_date, p.depth2_id, CONVERT(p.dsc USING utf8) AS dsc, dt.depth2, do.depth1 FROM product AS p INNER JOIN c_depth2 AS dt ON dt.id=p.depth2_id INNER JOIN c_depth1 AS do ON dt.depth1_id=do.id
(p.name LIKE concat('%', #{search}, '%') OR p.id LIKE concat('%', #{search}, '%'))
AND dt.id=#{depth2}
ORDER BY id DESC
LIMIT ${offset}, ${listCount}
